datatype Document = Document(movingAverage: real)
datatype Query = Query(upperBound: real, lowerBound: real)
class Collection {
  var documents: seq<Document>
  constructor()
    ensures documents == []
  {
    documents := [];
  }
  method CountDocuments(query: Query) returns (count: nat)
    ensures count <= |documents|
    ensures count == CountMatching(documents, query)
  {
    count := 0;
    var i := 0;

    while i < |documents|
      invariant 0 <= i <= |documents|
      invariant count == CountMatching(documents[..i], query)
      invariant count <= i
      decreases |documents| - i
    {
      var oldCount := count;
      var oldPrefix := documents[..i];

      if IsMatch(documents[i], query) {
        count := count + 1;
      }
      i := i + 1;

      assert documents[..i] == oldPrefix + [documents[i-1]];


      lemma_CountMatchingDistributes(oldPrefix, documents[i-1], query);


      assert CountMatching(documents[..i], query) == 
             CountMatching(oldPrefix, query) + (if IsMatch(documents[i-1], query) then 1 else 0);


      if IsMatch(documents[i-1], query) {
        assert count == oldCount + 1;
        assert oldCount == CountMatching(oldPrefix, query);
      } else {
        assert count == oldCount;
        assert oldCount == CountMatching(oldPrefix, query);
      }
    }
    assert documents[..|documents|] == documents;
  }
  lemma lemma_CountMatchingDistributes(prefix: seq<Document>, doc: Document, query: Query)
    ensures CountMatching(prefix + [doc], query) == 
            CountMatching(prefix, query) + (if IsMatch(doc, query) then 1 else 0)
    decreases |prefix|
  {
    if |prefix| == 0 {
      assert prefix + [doc] == [doc];
    } else {
      var first := prefix[0];
      var rest := prefix[1..];
      assert prefix == [first] + rest;
      assert prefix + [doc] == [first] + (rest + [doc]);

      lemma_CountMatchingDistributes(rest, doc, query);
    }
  }
  function CountMatching(docs: seq<Document>, query: Query): nat
    decreases |docs|
  {
    if |docs| == 0 then 0
    else (if IsMatch(docs[0], query) then 1 else 0) + 
         CountMatching(docs[1..], query)
  }
  predicate IsMatch(doc: Document, query: Query)
  {
    doc.movingAverage > query.lowerBound && 
    doc.movingAverage < query.upperBound
  }
}
