  datatype Column = Column(name: string, dataType: DataType, constraints: seq<Constraint>)
  datatype DataType = Int | Text | Timestamp | Double
  datatype Constraint = PrimaryKey | NotNull | Unique

  class DatabaseConnection {
    var isConnected: bool
    var currentSchema: Schema
    var tables: map<string, Table>
    constructor()
    {
      isConnected := false;
      tables := map[];
