    method CreateSchema(schemaName: string) returns (success: bool)
    {
      currentSchema := Schema(schemaName);
      success := true;
    }

    method DropTable(tableName: string) returns (success: bool)
    {
      if tableName in tables {
        tables := map k | k in tables && k != tableName :: tables[k];
        success := true;
      } else {
        success := false;
      }
    }

    method CreateTable(tableName: string, columns: seq<Column>) returns (success: bool)
