method FindAllPathsHelper(g: Graph, start: int, end: int, currentPath: Path) returns (paths: seq<Path>)
  requires g != map[]
  requires start in g.Keys && end in g.Keys
  requires |currentPath| > 0 && currentPath[0] == start
  requires forall i :: 0 <= i < |currentPath| ==> currentPath[i] in g.Keys
  requires forall i,j :: 0 <= i < j < |currentPath| ==> currentPath[i] != currentPath[j]
  requires forall k :: k in g.Keys ==> forall i :: 0 <= i < |g[k]| ==> g[k][i] in g.Keys
  requires |currentPath| <= |g.Keys|  
  decreases |g.Keys| - |currentPath|  
  ensures forall p :: p in paths ==>
    |p| > 0 && p[0] == start && p[|p|-1] == end &&
    (forall i :: 0 <= i < |p| ==> p[i] in g.Keys) &&
    (forall i,j :: 0 <= i < j < |p| ==> p[i] != p[j])
{
  var current := currentPath[|currentPath|-1];


  if current == end {
    if |currentPath| == |g.Keys| {

      return [currentPath];
    }
    return [];
  }

  paths := [];

  if current in g {
    var neighbors := g[current];
    var i := 0;
    while i < |neighbors|
      invariant 0 <= i <= |neighbors|
      invariant forall p :: p in paths ==>
        |p| > 0 && p[0] == start && p[|p|-1] == end &&
        (forall j :: 0 <= j < |p| ==> p[j] in g.Keys) &&
        (forall j,k :: 0 <= j < k < |p| ==> p[j] != p[k])
    {
      var next := neighbors[i];

      if !Contains(currentPath, next) {

        var newPath := currentPath + [next];

        if |newPath| <= |g.Keys| { 
          var newPaths := FindAllPathsHelper(g, start, end, newPath);

          paths := paths + newPaths;
        }
      }
      i := i + 1;
    }
  }
}
predicate Contains(s: seq<int>, x: int)
{
  exists i :: 0 <= i < |s| && s[i] == x
}

