datatype FitParameters = FitParams(a0: real, a1: real, a2: real)
function CubicFunction(p: FitParameters, x: real): real
{
    p.a0 * (x-1.0) - p.a1 * ((x-1.0) * (x-1.0)) + p.a2 * ((x-1.0) * (x-1.0) * (x-1.0)) + 1.0
}
function ComputeResidual(param: FitParameters, point: Point): real
{
    point.y - CubicFunction(param, point.x)
}
function SumSquaredResiduals(param: FitParameters, points: seq<Point>): real
    requires |points| > 0
{
    if |points| == 1 
    then var r := ComputeResidual(param, points[0]); r * r
    else 
        var r := ComputeResidual(param, points[0]);
        r * r + SumSquaredResiduals(param, points[1..])
}
method NormalizePoints(points: seq<Point>) returns (normalized: seq<Point>)
    requires |points| > 0
    ensures |normalized| == |points|
    ensures forall i :: 0 <= i < |normalized| ==> 0.0 <= normalized[i].x <= 1.0
{
    var minX := points[0].x;
    var maxX := points[0].x;


    var i := 0;
    while i < |points|
        invariant 0 <= i <= |points|
        invariant forall k :: 0 <= k < i ==> points[k].x >= minX
        invariant forall k :: 0 <= k < i ==> points[k].x <= maxX
    {
        if points[i].x < minX { minX := points[i].x; }
        if points[i].x > maxX { maxX := points[i].x; }
        i := i + 1;
    }

    normalized := [];
    i := 0;
    var range := maxX - minX;
    while i < |points|
        invariant 0 <= i <= |points|
        invariant |normalized| == i
        invariant forall k :: 0 <= k < i ==> 
            0.0 <= normalized[k].x <= 1.0
    {
        var normalizedX := if range == 0.0 then 0.0 
                          else (points[i].x - minX) / range;
        normalized := normalized + [Point(normalizedX, points[i].y)];
        i := i + 1;
    }
}
method EvaluateCubicFunction(params: FitParameters, xValues: seq<real>) returns (yValues: seq<real>)
    requires |xValues| > 0
    ensures |yValues| == |xValues|
{
    yValues := [];
    var i := 0;
    while i < |xValues|
        invariant 0 <= i <= |xValues|
        invariant |yValues| == i
