module KeyRules {

  type KeyRuleMap = map<string, seq<string>>

  method ReadLines(filePath: string) returns (lines: seq<string>)
  {


    lines := ["file1:0,1,2", "file2:3,4,5"];
  }

  function Split(s: string, delimiter: char): seq<string>
  {
    if s == "" then []
    else if s[0] == delimiter then Split(s[1..], delimiter)
    else 
      var restSplit := Split(s[1..], delimiter);
      if |restSplit| > 0 then
        [s[0..1] + restSplit[0]] + restSplit[1..]
      else
        [s[0..1]] + restSplit
  }

  method ReadKeyRules(keysPath: string) returns (keyRules: KeyRuleMap)
  {
    var lines := ReadLines(keysPath);
    keyRules := map[];
    for i := 0 to |lines|
    {
      var parts := Split(lines[i], ':');
      if |parts| == 2 {
        var key := parts[0];
        var values := Split(parts[1], ',');
        if |values| > 0 {
          keyRules := keyRules[key := values];
        }
      }
    }
  }
}

