datatype LogEntry = LogEntry(phase1Finished: bool, phase4Finished: bool)
datatype PlotterState = PlotterState(logs: seq<LogEntry>, totalCores: nat, usedCores: nat)
class Plotter {
  const CORES_PER_NEW_PLOT := 4
  const TOTAL_LOGICAL_CORES: nat
  var state: PlotterState
  constructor(cores: nat)
  {
    TOTAL_LOGICAL_CORES := cores;
    state := PlotterState([], cores, 0);
  }
  method CountUsedCores(logs: seq<LogEntry>) returns (usedCores: nat)
  {
    usedCores := 0;
    var i := 0;

    while i < |logs|
    {
      var plotCores := CORES_PER_NEW_PLOT;

      if logs[i].phase1Finished {
        plotCores := plotCores - 2;
      }
      if logs[i].phase4Finished {
        plotCores := plotCores - 2;
      }

      usedCores := usedCores + plotCores;
      i := i + 1;
    }
    usedCores := if usedCores <= TOTAL_LOGICAL_CORES then usedCores else TOTAL_LOGICAL_CORES;
  }
  method TryAddPlot() returns (success: bool)
            !success && state == old(state)
  {
    if state.usedCores + CORES_PER_NEW_PLOT <= TOTAL_LOGICAL_CORES {
      var newLog := LogEntry(false, false);
      state := PlotterState(state.logs + [newLog], 
                           state.totalCores, 
                           state.usedCores + CORES_PER_NEW_PLOT);
      return true;
    }
    return false;
  }
  method RunPlotterLoop() returns (finalState: PlotterState) 
  {
    while true
    {
      var usedCores := CountUsedCores(state.logs);
      state := PlotterState(state.logs, state.totalCores, usedCores);

      var added := TryAddPlot();
      if !added {
        break;
      }
    }
    return state;
  }
}

