datatype Item = Item(name: string, value: int, rarity: int, itemType: ItemType)
class Player {
  var gold: int
  var inventory: map<string, (Item, int)>  
  var charisma: int
  constructor()
  {
    gold := 0;
    charisma := 0;
    inventory := map[];
  }
}
method BuyItem(player: Player, item: Item, quantity: int) returns (success: bool)


{
  var adjustedPrice := CalculateAdjustedPrice(item.value, player.charisma, quantity);

  if player.gold >= adjustedPrice {

    player.gold := player.gold - adjustedPrice;


    var currentQuantity := 0;
    if item.name in player.inventory {
      currentQuantity := player.inventory[item.name].1;
    }
    player.inventory := player.inventory[item.name := (item, currentQuantity + quantity)];

    return true;
  }

  return false;
}
function CalculateAdjustedPrice(baseValue: int, charisma: int, quantity: int): int
{
  var discountedValue := if baseValue - (charisma * 2) > 1 then 
                           baseValue - (charisma * 2)
                         else 
                           1;
  discountedValue * quantity
}
method SellItem(player: Player, itemName: string, quantity: int) returns (success: bool)
{
  var item := player.inventory[itemName].0;
  var currentQuantity := player.inventory[itemName].1;


  var salePrice := (item.value * quantity) / 2 + (player.charisma * 2);


  player.gold := player.gold + salePrice;


