class LazyMap<T,U> {
  var data: seq<T>
  var f: T -> U

  predicate Valid()
    reads this
  {
    |data| >= 0
  }
  constructor(input: seq<T>, fn: T -> U)
    ensures Valid()
    ensures data == input
    ensures f == fn
  {
    data := input;
    f := fn;
  }
  function Length(): nat
    requires Valid()
    reads this
    ensures Length() == |data|
  {
    |data|
  }
  method Get(index: nat) returns (result: U)
    requires Valid()
    requires index < |data|
    ensures result == f(data[index])
  {
    result := f(data[index]);
  }
  method Slice(start: nat, end: nat) returns (result: LazyMap<T,U>)
    requires Valid()
    requires start <= end <= |data|
    ensures fresh(result)
    ensures result.Valid()
    ensures result.data == data[start..end]
    ensures result.f == f
  {
    var sliced := data[start..end];
    result := new LazyMap(sliced, f);
  }
  function ToString(): string
    requires Valid()
    reads this
    ensures ToString() != ""
  {
    "<LazyMap [" + ToStringHelper(0) + "]>"
  }
  function ToStringHelper(i: nat): string
    requires Valid()
    reads this
    decreases |data| - i
  {
    if i >= |data| then
      ""
    else if i == |data| - 1 then
      DataToString(data[i])
    else
      DataToString(data[i]) + ", " + ToStringHelper(i + 1)
  }
  function DataToString(x: T): string
}
method TestLazyMap()
