class NumberGuessingGame {
  method PlayGame(secretNumber: int, mode: Mode, customLow: int := 0, customHigh: int := 0) returns (tries: int, success: bool)
    requires mode == Custom ==> customLow <= customHigh
    requires mode == Easy ==> 1 <= secretNumber <= 10
    requires mode == Hard ==> 1 <= secretNumber <= 100 
    requires mode == Custom ==> customLow <= secretNumber <= customHigh
    ensures 1 <= tries <= 5
    ensures success <==> tries <= 4
  {
    var currentTries := 1;
    var found := false;
    var guess := 0;

    while currentTries <= 4 && !found 
