      invariant !found ==> currentTries <= 5
      invariant found ==> guess == secretNumber
      invariant currentTries > 4 ==> !found
      decreases 4 - currentTries, if found then 0 else 1
    {
      guess := GetGuess(mode, customLow, customHigh);

      if guess == secretNumber {
        found := true;
      } else {
        currentTries := currentTries + 1;
      }
    }

    tries := currentTries;
    success := found;
