datatype Position = Position(x: int, y: int)
datatype Player = Player(position: Position, walls: int)
datatype MoveType = Movement | HorizontalWall | VerticalWall 
datatype Move = Move(moveType: MoveType, position: Position)
datatype GameState = GameState(players: seq<Player>, walls: seq<Position>)
class Quoridor {
  var state: GameState


  constructor(players: seq<Player>, walls: seq<Position>)
  {
    state := GameState(players, walls);
  }

  predicate IsValidMove(move: Move, playerIndex: int)
  {

    if move.position.x < 0 || move.position.x >= 9 ||
       move.position.y < 0 || move.position.y >= 9 then
      false
    else
      match move.moveType
      case Movement =>

        true
      case HorizontalWall =>

        state.players[playerIndex].walls > 0
      case VerticalWall =>

        state.players[playerIndex].walls > 0
  }

  method ApplyMove(move: Move, playerIndex: int) returns (newState: GameState)
  {
    var players := state.players;
    var walls := state.walls;

    match move.moveType {
      case Movement =>

        var updatedPlayer := Player(move.position, players[playerIndex].walls);
        players := players[playerIndex := updatedPlayer];
      case HorizontalWall =>

        walls := walls + [move.position];
        var updatedPlayer := Player(
          players[playerIndex].position,
          players[playerIndex].walls - 1
        );
        players := players[playerIndex := updatedPlayer];
      case VerticalWall =>

        walls := walls + [move.position];
        var updatedPlayer := Player(
          players[playerIndex].position,
          players[playerIndex].walls - 1
        );
        players := players[playerIndex := updatedPlayer];
    }

    newState := GameState(players, walls);
  }

  method IsGameWon() returns (won: bool, winner: int)
  {
    won := false;
    winner := 0;


    if state.players[0].position.y == 8 {
      won := true;
      winner := 0;
    }

    else if state.players[1].position.y == 0 {
      won := true;
      winner := 1;
    }
  }
}

