datatype URLParam = 
  | StringParam(strValue: string)
  | IntParam(intValue: int) 
  | NoParam
class Route {
  var path: seq<string>  
  var paramType: URLParam
  var handlerName: string
  var routeName: string
  constructor(p: seq<string>, param: URLParam, handler: string, name: string)
  {
    path := p;
    paramType := param;
    handlerName := handler;
    routeName := name;
  }
}
class URLRouter {
  var routes: seq<Route>
  constructor()
  {
    routes := [];
  }

  method AddRoute(route: Route?)
  {
    routes := routes + [route];
  }

  method MatchPath(urlPath: seq<string>) returns (result: (Route?, URLParam))
  {
    var i := 0;
    while i < |routes|
    {
      var currentRoute := routes[i];
      if PathMatches(urlPath, currentRoute.path)
      {
        var param := ExtractParam(urlPath, currentRoute.paramType);
        return (currentRoute, param);
      }
      i := i + 1;
    }

    return (null, NoParam);
  }

  predicate PathMatches(urlPath: seq<string>, routePath: seq<string>)
  {
    |urlPath| == |routePath| &&
    forall i :: 0 <= i < |urlPath| ==>
      ((|routePath[i]| > 0 && routePath[i][0] == '<') || urlPath[i] == routePath[i])
  }

  method ExtractParam(urlPath: seq<string>, paramType: URLParam) returns (result: URLParam)
      case StringParam(_) => result.StringParam?
      case IntParam(_) => result.IntParam?
      case NoParam => result.NoParam?
  {


    return paramType;
  }
}

