datatype ProjectInfo = ProjectInfo(
  product: string,
  field: string, 
  platform: string,
  location: string,
  homepage: string,
  establishTime: string,
  status: string,
  tags: string,
  description: string,
  contact: string,
  leadership: seq<string>,
  logoUrl: string,
  url: string
)
class {:extern} ExternalOperations {
  constructor() {} 

  method {:axiom} FetchUrl(url: string) returns (content: string)
    requires url != ""
    ensures content != ""

  method {:axiom} SaveToDatabase(info: ProjectInfo)
    requires info.url != ""
}
class NewseedScraper {
  var external: ExternalOperations
  var crawledUrls: set<string>
  var allUrls: set<string>
  constructor()
    ensures fresh(external)
    ensures crawledUrls == {}
    ensures allUrls == {}
  {
    external := new ExternalOperations();
    crawledUrls := {};
    allUrls := {};
  }

  method GetUncrawledUrls() returns (urls: seq<string>)
    ensures forall u :: u in urls ==> u in allUrls && u !in crawledUrls
    ensures forall u :: u in allUrls && u !in crawledUrls ==> u in urls
  {
    var uncrawled := allUrls - crawledUrls;

    urls := [];
    var remaining := uncrawled;
    while remaining != {}
      decreases |remaining|
      invariant remaining <= uncrawled
      invariant forall u :: u in urls ==> u in uncrawled
      invariant forall u :: u in uncrawled ==> u in urls || u in remaining
    {
      var url :| url in remaining;
      urls := urls + [url];
      remaining := remaining - {url};
    }
  }

  method ParseUrl(url: string) returns (info: ProjectInfo)
    requires url != ""
    ensures info.url == url
  {
    var content := external.FetchUrl(url);



    info := ProjectInfo(
      "Product", 
      "Field",
      "Platform", 
      "Location",
      "Homepage",
      "EstablishTime",
      "Status",
      "Tags",
      "Description",
      "Contact",
      [],  
      "LogoUrl",
      url
    );
  }

  method ProcessUrls(urls: seq<string>) returns (results: seq<ProjectInfo>)
    requires forall i :: 0 <= i < |urls| ==> urls[i] != ""
    ensures |results| == |urls|
    ensures forall i :: 0 <= i < |results| ==> results[i].url == urls[i]
  {
    results := [];
    var i := 0;

    while i < |urls|
      invariant 0 <= i <= |urls|
      invariant |results| == i
      invariant forall j :: 0 <= j < i ==> results[j].url == urls[j]
    {
      var info := ParseUrl(urls[i]);
      results := results + [info];
      i := i + 1;
    }
  }
}

