  pixels: seq<seq<int>>,  
  width: nat,             
  height: nat             
)
datatype Size = Size(width: nat, height: nat)
class ImageProcessor {

  predicate ValidImage(img: Image)
  {
    && img.height == |img.pixels|
    && (img.height > 0 ==> img.width == |img.pixels[0]|)
    && forall i :: 0 <= i < img.height ==> |img.pixels[i]| == img.width
  }

  method NewImage(size: Size) returns (img: Image)
    requires size.width > 0 && size.height > 0
    ensures ValidImage(img)
    ensures img.width == size.width && img.height == size.height
    ensures forall i, j :: 
      0 <= i < img.height && 0 <= j < img.width ==> img.pixels[i][j] == 0
  {
    var pixels := seq(size.height, i => seq(size.width, j => 0));
    img := Image(pixels, size.width, size.height);
  }

  method ModifyImage(source: Image, targetSize: Size) returns (result: Image)
    requires ValidImage(source)
    requires targetSize.width > 0 && targetSize.height > 0
    ensures ValidImage(result)
    ensures result.width == targetSize.width && result.height == targetSize.height
  {

    var xOffset := (targetSize.width - source.width) / 2;
    var yOffset := (targetSize.height - source.height) / 2;


    result := NewImage(targetSize);


    var i := 0;
    while i < source.height
      invariant 0 <= i <= source.height
      invariant ValidImage(result)
      invariant result.width == targetSize.width 
      invariant result.height == targetSize.height
    {
      var j := 0;
      while j < source.width
        invariant 0 <= j <= source.width
        invariant ValidImage(result)
        invariant result.width == targetSize.width
        invariant result.height == targetSize.height
      {
        if 0 <= yOffset + i < result.height && 0 <= xOffset + j < result.width {

          var newPixels := result.pixels[yOffset + i := 
            result.pixels[yOffset + i][xOffset + j := source.pixels[i][j]]];
          result := Image(newPixels, result.width, result.height);
        }
        j := j + 1;
      }
      i := i + 1;
    }
  }

  method ModifyImages(images: seq<Image>, targetSize: Size) returns (results: seq<Image>)
    requires forall img :: img in images ==> ValidImage(img)
    requires targetSize.width > 0 && targetSize.height > 0
    ensures |results| == |images|
    ensures forall i :: 0 <= i < |results| ==> ValidImage(results[i])
    ensures forall i :: 0 <= i < |results| ==> 
      results[i].width == targetSize.width && results[i].height == targetSize.height
  {
    results := [];
    var i := 0;
    while i < |images|
      invariant 0 <= i <= |images|
      invariant |results| == i
      invariant forall j :: 0 <= j < i ==> ValidImage(results[j])
      invariant forall j :: 0 <= j < i ==> 
        results[j].width == targetSize.width && results[j].height == targetSize.height
    {
      var modifiedImage := ModifyImage(images[i], targetSize);
      results := results + [modifiedImage];
