class DataFrame {
  var rows: seq<Row>
  ghost var Valid: bool

  constructor()
  {
    rows := [];
    Valid := true;
  }
}
class Row {
  var values: map<string, string>
  ghost var Valid: bool
  constructor()
  {
    values := map[];
    Valid := true;
  }
}
method ProcessTargetUniprot(df: DataFrame?, proteins: DataFrame?) returns (result: DataFrame?)
{
  result := new DataFrame();
  var filteredRows := [];

  var i := 0;
  while i < |df.rows|
  {
    var row := df.rows[i];
    if "target_uniprot" in row.values && row.values["target_uniprot"] != ""
    {
      var targetUniprot := row.values["target_uniprot"];
      var found := false;

      var j := 0;
      while j < |proteins.rows|
      {
        if "uniprot" in proteins.rows[j].values && 
           proteins.rows[j].values["uniprot"] == targetUniprot
        {
          found := true;
          break;
        }
        j := j + 1;
      }

      if found {
        filteredRows := filteredRows + [row];
      }
    }
    i := i + 1;
  }

  result.rows := filteredRows;
  result.Valid := true;
}
method ProcessLigandGeneSymbol(df: DataFrame?, genes: DataFrame?) returns (result: DataFrame?)
{
  result := new DataFrame();
  var filteredRows := [];

  var i := 0;
  while i < |df.rows|
  {
    var row := df.rows[i];
    if "ligand_gene_symbol" in row.values && row.values["ligand_gene_symbol"] != ""
    {
      var geneSymbol := row.values["ligand_gene_symbol"];
      var found := false;

      var j := 0;
      while j < |genes.rows|
      {
        if "gene_name" in genes.rows[j].values &&
           genes.rows[j].values["gene_name"] == geneSymbol
        {
          found := true;
          break;
        }
        j := j + 1;
      }

      if found {
        filteredRows := filteredRows + [row];
      }
    }
    i := i + 1;
  }

  result.rows := filteredRows;
  result.Valid := true;
}
method CreateCellphoneFormat(df: DataFrame?) returns (result: DataFrame?)
{
  result := new DataFrame();
  var newRows := [];

  var i := 0;
  while i < |df.rows|
  {
    var row := new Row();
    if "uniprot" in df.rows[i].values && "target_uniprot" in df.rows[i].values {
      row.values := map[
        "uniprot_1" := df.rows[i].values["uniprot"],
        "uniprot_2" := df.rows[i].values["target_uniprot"],
        "annotation_strategy" := "guidetopharmacology.org",
        "iuphar" := "true"
      ];
      row.Valid := true;
      newRows := newRows + [row];
    }
    i := i + 1;
  }

  result.rows := newRows;
  result.Valid := true;
}
method ProcessInteractions(
  iuphar: DataFrame?, 
  genes: DataFrame?, 
  proteins: DataFrame?
) returns (result: DataFrame?)
{

  var filtered := FilterBySpecies(iuphar);


  filtered := ProcessTargetUniprot(filtered, proteins);


  filtered := ProcessLigandGeneSymbol(filtered, genes);


  var cellphoneFormat := CreateCellphoneFormat(filtered);


  result := DropDuplicates(cellphoneFormat);
}
method FilterBySpecies(df: DataFrame?) returns (result: DataFrame?)
{
  result := new DataFrame();
  var filteredRows := [];

  var i := 0;
  while i < |df.rows|
  {
    var row := df.rows[i];
    if "target_species" in row.values && "ligand_species" in row.values &&
       row.values["target_species"] == "Human" && 
       row.values["ligand_species"] == "Human"
    {
      filteredRows := filteredRows + [row];
    }
    i := i + 1;
  }

  result.rows := filteredRows;
  result.Valid := true;
}
method DropDuplicates(df: DataFrame?) returns (result: DataFrame?)
{
  result := new DataFrame();
  var seen := map[];
  var uniqueRows := [];

  var i := 0;
  while i < |df.rows|
  {
    var row := df.rows[i];
    if "uniprot_1" in row.values && "uniprot_2" in row.values
    {
      var key := (row.values["uniprot_1"], row.values["uniprot_2"]);
      if key !in seen {
        seen := seen[key := true];
        uniqueRows := uniqueRows + [row];
      }
    }
    i := i + 1;
  }

  result.rows := uniqueRows;
  result.Valid := true;
}

