class DateTime {
  var year: int
  var month: int
  var day: int
  var hour: int
  var minute: int
  var second: int

  predicate Valid()
  {
    && 1 <= month <= 12
    && 1 <= day <= 31  
    && 0 <= hour <= 23
    && 0 <= minute <= 59
    && 0 <= second <= 59
  }

  constructor(y: int, m: int, d: int, h: int, min: int, s: int)
  {
    year := y;
    month := m;
    day := d;
    hour := h;
    minute := min;
    second := s;
  }


  method GetWeekday() returns (weekday: int)
  {
    var m := if month <= 2 then month + 12 else month;
    var y := if month <= 2 then year - 1 else year;

    var k := y % 100;
    var j := y / 100;

    weekday := (day + ((13 * (m + 1)) / 5) + k + (k / 4) + (j / 4) - (2 * j)) % 7;
    if weekday < 0 { weekday := weekday + 7; }

    weekday := (weekday + 5) % 7;
  }

  method AddSeconds(seconds: int) returns (newDateTime: DateTime?)
  {

    var newSecond := second + seconds;
    if 0 <= newSecond <= 59 {
      newDateTime := new DateTime(year, month, day, hour, minute, newSecond);
      return;
    }

    return null;
  }

  method AddDays(days: int) returns (newDateTime: DateTime?)
  {

    var newDay := day + days;
    if 1 <= newDay <= 31 {  
      newDateTime := new DateTime(year, month, newDay, hour, minute, second);
      return;
    }

    return null;
  }

  method PrintInfo()
  {
    print "Year: ", year, "\n";
    print "Month: ", month, "\n";
    var w := GetWeekday();
    print "Week: ", w, "\n";
  }
}
method Main()
{

  var dt := new DateTime(2024, 3, 15, 12, 0, 0);

  dt.PrintInfo();

  var futureDate := dt.AddDays(5);
  if futureDate != null {
    print "+5 days: ";
    futureDate.PrintInfo();
  }

  var futureTime := dt.AddSeconds(5);
  if futureTime != null {
    print "+5 seconds: ";
    futureTime.PrintInfo();
  }
}

