class DataContainer {
    var data: array2?<real>
    var frequency: real  
    var channels: array?<string>

    ghost predicate Valid()
    {
        data != null &&
        channels != null &&
        data.Length1 > 0 &&
        data.Length0 == channels.Length &&
        frequency > 0.0
    }

    constructor FromArray(inputData: array2?<real>, freq: real, channelNames: array?<string>)
    {
        var newData := new real[inputData.Length0, inputData.Length1];
        var newChannels := new string[channelNames.Length];

        var i := 0;
        var j := 0;
        while i < inputData.Length0
        {
            j := 0;
            while j < inputData.Length1
            {
                newData[i,j] := inputData[i,j];
                j := j + 1;
            }
            i := i + 1;
        }

        i := 0;
        while i < channelNames.Length
        {
            newChannels[i] := channelNames[i];
            i := i + 1;
        }

        data := newData;
        channels := newChannels;
        frequency := freq;
    }

    method GetDuration() returns (duration: real)
    {
        duration := data.Length1 as real / frequency;
    }

    method GetTimeSlice(start: real, end: real) returns (slice: DataContainer?)
    {
        var startSample := (start * frequency).Floor as int;
        var endSample := (end * frequency).Floor as int;

        if startSample >= data.Length1 || endSample > data.Length1 || endSample <= startSample {
            slice := null;
            return;
        }

        var newData := new real[data.Length0, endSample - startSample];
        var newChannels := new string[channels.Length];

        var i := 0;
        while i < data.Length0
        {
            var j := 0;
            while j < endSample - startSample
            {
                newData[i,j] := data[i, startSample + j];
                j := j + 1;
            }
            newChannels[i] := channels[i];
            i := i + 1;
        }

        slice := new DataContainer.FromArray(newData, frequency, newChannels);
    }
}

