  score: real,        
  precisions: seq<real>, 
  bp: real            
)
function MyLog(x: real): real 
  requires x >= 0.0
{
  if x == 0.0 then -9999999999.0 else -1.0 
}
function MyExp(x: real): real {
  1.0 
}
method ComputeBleu(stats: BleuStats, effectiveOrder: int) returns (result: BleuResult)
  requires |stats.correct| == |stats.total|
  requires 1 <= effectiveOrder <= |stats.correct|
  requires forall i :: 0 <= i < |stats.correct| ==> stats.total[i] >= stats.correct[i] >= 0
  requires stats.refLen >= 0 && stats.sysLen >= 0
  ensures 0.0 <= result.score <= 1.0
{

  var precisions := new real[|stats.correct|];
  var i := 0;
  while i < |stats.correct|
    invariant 0 <= i <= |stats.correct|
    invariant precisions.Length == |stats.correct|
    invariant forall j :: 0 <= j < i ==> 0.0 <= precisions[j] <= 100.0
  {
    if stats.total[i] == 0 {
      precisions[i] := 0.0;
    } else {
      precisions[i] := 100.0 * (stats.correct[i] as real) / (stats.total[i] as real);
    }
    i := i + 1;
  }

  var bp: real;
  if stats.sysLen < stats.refLen && stats.sysLen > 0 {
    bp := MyExp(1.0 - (stats.refLen as real) / (stats.sysLen as real));
  } else {
    bp := 1.0;
  }

  var logSum := 0.0;
  i := 0;
  while i < effectiveOrder
    invariant 0 <= i <= effectiveOrder
    invariant -9999999999.0 * (i as real) <= logSum <= 0.0
  {
    logSum := logSum + MyLog(precisions[i] / 100.0);
    i := i + 1;
  }
  var score := bp * MyExp(logSum / (effectiveOrder as real));

  result := BleuResult(score, precisions[..], bp);
}
method ExtractNgrams(tokens: seq<string>, minOrder: int, maxOrder: int) returns (counts: map<string, int>)
  requires 1 <= minOrder <= maxOrder
  requires |tokens| >= maxOrder  
  ensures forall ngram :: ngram in counts ==> counts[ngram] > 0
{
  counts := map[];

  var order := minOrder;
  while order <= maxOrder
    invariant minOrder <= order <= maxOrder + 1
    invariant forall ngram :: ngram in counts ==> counts[ngram] > 0
  {
    var i := 0;
    while i <= |tokens| - order
      invariant 0 <= i <= |tokens| - order + 1
      invariant forall ngram :: ngram in counts ==> counts[ngram] > 0
    {

      var ngram := "";
      var j := 0;
      while j < order
        invariant 0 <= j <= order
      {
        if j > 0 { ngram := ngram + " "; }
        ngram := ngram + tokens[i+j];
        j := j + 1;
      }

      counts := counts[ngram := if ngram in counts then counts[ngram] + 1 else 1];
      i := i + 1;
    }
    order := order + 1;
  }
