            tokenizer.indexWord[i] in tokenizer.wordIndex.Keys &&
            tokenizer.wordIndex[tokenizer.indexWord[i]] == i
        ensures forall c :: c in tokenizer.wordIndex ==>
            tokenizer.indexWord[tokenizer.wordIndex[c]] == c
    {
        var chars := ['', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', ' '];
        var wordIndex: map<char,int> := map[];
        var indexWord: map<int,char> := map[];
        var i := 0;
        while i < |chars|
            invariant 0 <= i <= |chars|
            invariant forall k :: 0 <= k < i ==> chars[k] in wordIndex
            invariant forall k :: 0 <= k < i ==> k in indexWord
            invariant forall k :: 0 <= k < i ==> wordIndex[chars[k]] == k
            invariant forall k :: 0 <= k < i ==> indexWord[k] == chars[k]
            invariant forall k :: k in wordIndex ==> 0 <= wordIndex[k] < i
            invariant forall k :: k in indexWord ==> 0 <= k < i
            invariant wordIndex.Keys == set k | 0 <= k < i :: chars[k]
            invariant indexWord.Values == set k | 0 <= k < i :: chars[k]
            invariant forall k :: k in indexWord ==> 
                indexWord[k] in wordIndex.Keys && wordIndex[indexWord[k]] == k
            invariant forall c :: c in wordIndex ==> 
                indexWord[wordIndex[c]] == c
        {
            wordIndex := wordIndex[chars[i] := i];
            indexWord := indexWord[i := chars[i]];
            i := i + 1;
        }
        tokenizer := TokenizerState(wordIndex, indexWord);
    }
    method CleanText(text: string) returns (cleaned: string)
        ensures |cleaned| <= |text|
        ensures forall i :: 0 <= i < |cleaned| ==> 
            (cleaned[i] in " " )
    {
        var result := "";
        var i := 0;

        while i < |text|
            invariant 0 <= i <= |text|
