method checkGreater(arr:array<int>, number:int) returns (res:bool)
{
  res := true;
  var i := 0;
  while i < arr.Length
    invariant 0 <= i <= arr.Length
    invariant res == forall j :: 0 <= j < i ==> number > arr[j]
  {
    if number <= arr[i] {
      res := false;
      return;
    }
    i := i + 1;
  }
}
