method firstRepeatedChar(str1: string) returns (res: char)
{
    var seen := set char{};
    var i: int := 0;
    while i < |str1|
        invariant 0 <= i <= |str1|
        invariant forall j:int :: 0 <= j < i ==> str1[j] in seen
        decreases |str1| - i
    {
        var c := str1[i];
        if c in seen {
            res := c;
            return;
        }
        seen := seen + {c};
        i := i + 1;
    }
    // If you reach here, there is no repeated char; we return a default value.
    // (You can change as needed. Here returning '\0' as a placeholder.)
    res := '\0';
}

