method firstRepeatedChar(str1: string) returns (res: char)
{
    var seen := new set<char>;
    var len := |str1|;
    var i: int := 0;
    while i < len
        invariant 0 <= i <= len
        invariant seen <= set j | 0 <= j < i :: str1[j]
    {
        var c := str1[i];
        if c in seen {
            res := c;
            return;
        }
        seen := seen + {c};
        i := i + 1;
    }
    // If no repeated char, just return '\0' (null character)
    res := '\0';
}

