method replaceSpaces(str: string) returns (res: string)
{
  var builder := new seq<char>();
  var i := 0;
  while i < |str|
    invariant 0 <= i <= |str|
    invariant |builder| == |str[..i]| + (|str[..i].Filter(c => c == ' ')| * 2)
  {
    if str[i] == ' ' {
      builder := builder + ['%', '2', '0'];
    } else {
      builder := builder + [str[i]];
    }
    i := i + 1;
  }
  res := builder[..].AsString();
}

